char start_wizard_window_title[ZXVISION_MAX_WINDOW_TITLE];

//Total de preguntas sin tener en cuenta los condicionales (como el de zx desktop)
int start_wizard_total_steps=7;


void menu_first_start_wizard_language(MENU_ITEM_PARAMETERS)
{

    //Nota: estoy habilitando aqui algunas opciones que en ZEsarUX no vienen activadas por defecto (como visibilidad de tooltips por ejemplo)

    //Textos en los diferentes idiomas
    char *mensaje_zxdesktop_english="ZX Desktop allows you to enlarge the main window by adding a Desktop area "
        "so you can have icons and menus located there. Do you want to enable ZX Desktop?";
    char *mensaje_zxdesktop_spanish="ZX Desktop permite agrandar la ventana principal añadiendo una zona de escritorio "
        "de tal manera que puedes tener iconos y menús localizados ahí. Quieres activar ZX Desktop?";
    char *mensaje_zxdesktop_catalan="ZX Desktop permet engrandir la finestra principal afegint una zona d'escriptori "
        "de tal manera que pots tenir icones i menús localizats allà. Vols activar ZX Desktop?";

    char *mensaje_autosnapshot_spanish="Quieres autograbar el estado de la máquina emulada (snapshot) al salir? "
        "Y se restaura automáticamente al inicio";
    char *mensaje_autosnapshot_catalan="Vols autogravar l'estat de la màquina emulada (snapshot) al sortir? "
        "I es restaura automàticament a l'inici";
    char *mensaje_autosnapshot_english="Do you want to autosave the emulated machine state (snapshot) on exit? "
        "And it will be restored automatically on start";


    char *mensaje_realvideo_spanish="Quieres activar Real Video? Es una opción para emular a la perfección los efectos de pantalla de la máquina emulada. "
        "Puede usar un poco más de CPU pero generalmente es conveniente activarlo";
    char *mensaje_realvideo_catalan="Vols activar Real Video? Es una opció per emular a la perfecció els efectes de pantalla de la màquina emulada. "
        "Pot usar una mica més de CPU però habitualment és convenient activar-ho";
    char *mensaje_realvideo_english="Do you want to enable Real Video? It's an option to perfectly emulate the screen effects of the emulated machine. "
        "It may use a little more CPU, but it's generally a good idea to enable it";

    char *mensaje_firstaid_spanish="Quieres activar mensajes de ayuda de 'Primeros Auxilios'? Son mensajes de ayuda que aparecen al iniciar ZEsarUX "
        "y también al abrir ciertos menús";
    char *mensaje_firstaid_catalan="Vols activar missatges d'ajuda de 'Primers Auxilis'? Son missatges d'ajuda que apareixen al iniciar ZEsarUX "
        "i també al obrir alguns menús";
    char *mensaje_firstaid_english="Do you want to enable 'First aid' help messages? They are help messages that appear on starting ZEsarUX "
        "and also when opening some menus";

    char *mensaje_tooltips_spanish="Quieres activar mensajes de ayuda 'Tooltips'? Son mensajes de ayuda que aparecen al elegir entradas de menús";
    char *mensaje_tooltips_catalan="Vols activar missatges d'ajuda 'Tooltips'? Son missatges d'ajuda que apareixen al escollir entrades de menús";
    char *mensaje_tooltips_english="Do you want to enable 'Tooltips' help messages? They are help messages that appear on selecting menu items";


    char *mensaje_atajos_spanish="Quieres activar la visualización de los atajos de teclado? Permite seleccionar entradas de menú pulsando simplemente una tecla";
    char *mensaje_atajos_catalan="Vols activar la visualització de les dreçeres de teclat? Permet seleccionar entrades de menú prement simplement una tecla";
    char *mensaje_atajos_english="Do you want to enable visible shortcuts? It allows to select menu entries by pressing a key";

    char *mensaje_fin_spanish="Finalizado el asistente inicial, puedes empezar a usar ZEsarUX.\n"
        "Para abrir el menú, puedes usar F5, pulsar el botón izquierdo del ratón sobre el fondo, o pulsar en alguno de los iconos de menú "
        "superiores si has habilitado ZX Desktop.\n"
        "Puedes ahora cerrar esta ventana pulsando ESC, ENTER o haciendo click izquierdo con el ratón "
        "en el botón superior izquierdo de la ventana. Que lo disfrutes!";

    char *mensaje_fin_catalan="Finalitzat l'assistent inicial, pots començar a utilitzar ZEsarUX.\n"
        "Per obrir el menú, pots fer servir F5, prémer el botó esquerre del ratolí sobre el fons, o prémer alguna de les icones de menú "
        "superiors si has habilitat ZX Desktop.\n"
        "Pots ara tancar aquesta finestra prement ESC, ENTER o fent clic esquerre amb el ratolí "
        "al botó superior esquerre de la finestra. Que ho gaudeixis!";

    char *mensaje_fin_english="After completing the initial wizard, you can start using ZEsarUX.\n"
        "To open the menu, you can use F5, left-click on the background, or click on one of the menu icons at the top "
        "if you have enabled ZX Desktop.\n"
        "You can now close this window by pressing ESC, ENTER, or left-clicking the mouse "
        "on the top left button of the window. Enjoy!";


    //Punteros a los mensajes del idioma correspondiente
    char *mensaje_zxdesktop;
    mensaje_zxdesktop=mensaje_zxdesktop_english;

    char *mensaje_realvideo;
    mensaje_realvideo=mensaje_realvideo_english;

    char *mensaje_autosnapshot;
    mensaje_autosnapshot=mensaje_autosnapshot_english;

    char *mensaje_firstaid;
    mensaje_firstaid=mensaje_firstaid_english;

    char *mensaje_tooltips;
    mensaje_tooltips=mensaje_tooltips_english;

    char *mensaje_atajos;
    mensaje_atajos=mensaje_atajos_english;

    char *mensaje_fin;
    mensaje_fin=mensaje_fin_english;

    int current_step=2;

    switch (valor_opcion) {
        case 0:
            //Aunque este es el lenguaje por defecto, pero por si acaso lo definimos aqui
            gui_language=GUI_LANGUAGE_DEFAULT;
            mensaje_zxdesktop=mensaje_zxdesktop_english;
            mensaje_realvideo=mensaje_realvideo_english;
            mensaje_autosnapshot=mensaje_autosnapshot_english;
            mensaje_firstaid=mensaje_firstaid_english;
            mensaje_tooltips=mensaje_tooltips_english;
            mensaje_atajos=mensaje_atajos_english;
            mensaje_fin=mensaje_fin_english;
        break;

        case 1:
            gui_language=GUI_LANGUAGE_SPANISH;
            mensaje_zxdesktop=mensaje_zxdesktop_spanish;
            mensaje_realvideo=mensaje_realvideo_spanish;
            mensaje_autosnapshot=mensaje_autosnapshot_spanish;
            mensaje_firstaid=mensaje_firstaid_spanish;
            mensaje_tooltips=mensaje_tooltips_spanish;
            mensaje_atajos=mensaje_atajos_spanish;
            mensaje_fin=mensaje_fin_spanish;
        break;

        case 2:
            gui_language=GUI_LANGUAGE_CATALAN;
            mensaje_zxdesktop=mensaje_zxdesktop_catalan;
            mensaje_realvideo=mensaje_realvideo_catalan;
            mensaje_autosnapshot=mensaje_autosnapshot_catalan;
            mensaje_firstaid=mensaje_firstaid_catalan;
            mensaje_tooltips=mensaje_tooltips_catalan;
            mensaje_atajos=mensaje_atajos_catalan;
            mensaje_fin=mensaje_fin_catalan;
        break;
    }



    //Preguntar si realvideo
    int opcion_activar_realvideo=1;
    sprintf(start_wizard_window_title,"%s %d/%d",menu_get_string_language("Welcome"),current_step++,start_wizard_total_steps);
    zxvision_menu_generic_message_setting(start_wizard_window_title,mensaje_realvideo,
        menu_get_string_language("Enabled"),&opcion_activar_realvideo);

    if (salir_todos_menus) return;

    if (opcion_activar_realvideo) {
         if (rainbow_enabled.v==0) enable_rainbow();
    }


    //Preguntar si autoguardado/autocarga snapshot
    int opcion_autosnapshot=1;
    sprintf(start_wizard_window_title,"%s %d/%d",menu_get_string_language("Welcome"),current_step++,start_wizard_total_steps);
    zxvision_menu_generic_message_setting(start_wizard_window_title,mensaje_autosnapshot,
        menu_get_string_language("Enabled"),&opcion_autosnapshot);

    if (salir_todos_menus) return;

    autosave_snapshot_on_exit.v=opcion_autosnapshot;
    autoload_snapshot_on_start.v=opcion_autosnapshot;


    //Preguntar si first aid
    int opcion_activar_first_aid=1;
    sprintf(start_wizard_window_title,"%s %d/%d",menu_get_string_language("Welcome"),current_step++,start_wizard_total_steps);
    zxvision_menu_generic_message_setting(start_wizard_window_title,mensaje_firstaid,
        menu_get_string_language("Enabled"),&opcion_activar_first_aid);

    if (salir_todos_menus) return;

    if (!opcion_activar_first_aid) {
        menu_disable_first_aid.v=1;
    }


    //Preguntar si tooltips
    int opcion_activar_tooltips=0;
    sprintf(start_wizard_window_title,"%s %d/%d",menu_get_string_language("Welcome"),current_step++,start_wizard_total_steps);
    zxvision_menu_generic_message_setting(start_wizard_window_title,mensaje_tooltips,
        menu_get_string_language("Enabled"),&opcion_activar_tooltips);

    if (salir_todos_menus) return;

    tooltip_enabled.v=opcion_activar_tooltips;


    //Preguntar si visibilidad atajos teclado
    int opcion_activar_atajos=1;
    sprintf(start_wizard_window_title,"%s %d/%d",menu_get_string_language("Welcome"),current_step++,start_wizard_total_steps);
    zxvision_menu_generic_message_setting(start_wizard_window_title,mensaje_atajos,
        menu_get_string_language("Enabled"),&opcion_activar_atajos);

    if (salir_todos_menus) return;

    menu_force_writing_inverse_color.v=opcion_activar_atajos;


    //Preguntar si ZX Desktop. Este al final para no marear mucho al usuario con el cambio de posicion de la ventana
    if (scr_driver_can_ext_desktop() ) {
        int opcion_activar_zxdesktop=1;
        sprintf(start_wizard_window_title,"%s %d/%d",menu_get_string_language("Welcome"),current_step++,start_wizard_total_steps);
        zxvision_menu_generic_message_setting(start_wizard_window_title,mensaje_zxdesktop,
            menu_get_string_language("Enabled"),&opcion_activar_zxdesktop);

        if (salir_todos_menus) return;

        if (opcion_activar_zxdesktop) {
            //No esta habilitado, pero por si acaso recomprobar
            if (!screen_ext_desktop_enabled) {
                enable_zxdesktop_and_background();
            }
        }
    }


    //Final del asistente
    sprintf(start_wizard_window_title,"%s %d/%d",menu_get_string_language("Welcome"),current_step++,start_wizard_total_steps);
    menu_generic_message(start_wizard_window_title,mensaje_fin);
    salir_todos_menus=1;

}

