zxvision_window *menu_view_basic_listing_window;

//1 linea final en blanco para separar
#define VIEW_BASIC_HEADER_LINES 5

#define MENU_VIEW_BASIC_LISTING_LINE_SETTINGS 0
#define MENU_VIEW_BASIC_LISTING_LINE_FIND 1
#define MENU_VIEW_BASIC_LISTING_LINE_POINTER 2
#define MENU_VIEW_BASIC_LISTING_LINE_LINE 3

//1024kb al descomponer tokens en texto creo que es mas que suficiente
#define VIEW_BASIC_MAX_BASIC_TEXT (1024*1024)

//Mas que suficiente creo contando lineas que se cortan y van a la siguiente
#define VIEW_BASIC_MAX_LINES_FINAL (9999*10)


//Para poder hacer view basic desde direcciones de memoria
z80_bit menu_view_basic_listing_memory_enabled={0};
int menu_view_basic_listing_memory_pointer=23755;  //0x5cde=23774 primer bloque basic cargado en Cargador Azul
int menu_view_basic_listing_memory_length=10000;

z80_long_int menu_view_basic_listing_last_crc32=0;


//Calcular crc32 del bloque listado por saber si se modifica para volver a renderizar
z80_long_int menu_view_basic_listing_get_crc32(void)
{


    int start_address=menu_view_basic_listing_memory_pointer;
    int length=menu_view_basic_listing_memory_length;

    if (length<1) {
      //escribir_socket(misocket,"ERROR. Length must be >0");
      return 0;
    }


    //Copiar contenido memoria segun memory zone activa a buffer de memoria temporal
    z80_byte *memoria_temporal;
    memoria_temporal=malloc(length);
    if (memoria_temporal==NULL) cpu_panic("Can not allocate memory for crc32 calculation");

    int longitud_copiar=length;
    int i;

    for (i=0;longitud_copiar>0;i++,longitud_copiar--) {
      z80_byte byte_leido=peek_byte_no_time(start_address+i);
      memoria_temporal[i]=byte_leido;
    }



    z80_long_int crc32=util_crc32_calculation(0,memoria_temporal,length);


    free(memoria_temporal);

    return crc32;

}

//Definir parametros basic y ademas establecer variables de inico y final segun si custom pointer
void menu_view_basic_listing_get_basic_parameters(view_basic_parameters *parameters)
{


    debug_view_basic_prepare_parameters(parameters);

    if (menu_view_basic_listing_memory_enabled.v) {
        parameters->dir_inicio_linea=menu_view_basic_listing_memory_pointer;
        parameters->final_basic=menu_view_basic_listing_memory_pointer+menu_view_basic_listing_memory_length;
    }

    else {
        menu_view_basic_listing_memory_pointer=parameters->dir_inicio_linea;
        menu_view_basic_listing_memory_length=parameters->final_basic-parameters->dir_inicio_linea;
    }


}

