//Punteros a las diferentes lineas
char **menu_view_basic_listing_punteros_lineas=NULL;

//Donde almacenar las lineas ya troceadas
char *menu_view_basic_listing_buffer_lineas=NULL;

int menu_view_basic_listing_total_lineas=0;

void menu_view_basic_listing_print_basic(char *results_buffer,zxvision_window *w)
{
    //trocear
    //Punteros a cada linea de esas
    //Esto es muy maximo, pero podria pasar
    //Un maximo de VIEW_BASIC_MAX_LINES_FINAL lineas

    //char *punteros_lineas[VIEW_BASIC_MAX_LINES_FINAL];
    if (menu_view_basic_listing_punteros_lineas!=NULL) {
        free(menu_view_basic_listing_punteros_lineas);
        menu_view_basic_listing_punteros_lineas=NULL;
    }

    if (menu_view_basic_listing_punteros_lineas==NULL) {
        menu_view_basic_listing_punteros_lineas=util_malloc(VIEW_BASIC_MAX_LINES_FINAL*sizeof(char *),"Can not allocate memory for basic lines");
    }

    //Maximo de linea contando 0 del final
    int max_line_length=w->total_width;

    //maximo permitido en ancho
    int maxima_longitud=max_line_length-1;

    //printf("antes de malloc buffer lineas\n");
    if (menu_view_basic_listing_buffer_lineas!=NULL) {
        //printf("Liberando menu_view_basic_listing_buffer_lineas\n");
        free(menu_view_basic_listing_buffer_lineas);
        menu_view_basic_listing_buffer_lineas=NULL;
    }

    if (menu_view_basic_listing_buffer_lineas==NULL) {
        //printf("Asignando menu_view_basic_listing_buffer_lineas\n");
        menu_view_basic_listing_buffer_lineas=util_malloc(VIEW_BASIC_MAX_LINES_FINAL*max_line_length,"Can not allocate memory for basic lines");
    }


    //Inicializar punteros a lineas
    int i;
    for (i=0;i<VIEW_BASIC_MAX_LINES_FINAL;i++) {
        //printf("puntero linea %d\n",i);
        int offset_linea=i*max_line_length;
        menu_view_basic_listing_punteros_lineas[i]=&menu_view_basic_listing_buffer_lineas[offset_linea];
    }


    menu_view_basic_listing_total_lineas=zxvision_generic_message_aux_justificar_lineas(results_buffer,strlen(results_buffer),maxima_longitud,menu_view_basic_listing_punteros_lineas);

    //Reajustar alto total ventana
    //Darle lineas en blanco al final para que se pueda hacer follow de lineas hacia el final tambien (sino el scroll no bajaria mas)
    int aumentar_tamanyo=w->visible_height-VIEW_BASIC_HEADER_LINES-2-1;
    zxvision_set_total_height(w,VIEW_BASIC_HEADER_LINES+menu_view_basic_listing_total_lineas+aumentar_tamanyo);

    for (i=0;i<menu_view_basic_listing_total_lineas;i++) {
        //printf("linea %d : %s\n",i,punteros_lineas[i]);
        zxvision_print_string_defaults_fillspc(w,1,VIEW_BASIC_HEADER_LINES+i,menu_view_basic_listing_punteros_lineas[i]);
    }

    //limpiar las lineas de margen por debajo
    for (;i<menu_view_basic_listing_total_lineas+aumentar_tamanyo;i++) {
        zxvision_print_string_defaults_fillspc(w,1,VIEW_BASIC_HEADER_LINES+i,"");
    }

}

//Si contenido se ha modificado y hay que hacer dump de nuevo
int menu_view_basic_listing_modified=0;

int menu_view_basic_contador_segundo_anterior=0;

char *menu_view_basic_listing_results_buffer=NULL;

z80_bit menu_view_basic_listing_follow_current_line={0};

int menu_view_basic_listing_follow_line_last=-1;

//Retorna indice de linea en array si encontrada
//-1 si no encontrada
int menu_view_basic_listing_find_line_aux(int linea_buscar)
{

    int i;

    char buffer_linea_buscar[7];
    //Numero y espacio
    sprintf(buffer_linea_buscar,"%5d ",linea_buscar);

    for (i=0;i<menu_view_basic_listing_total_lineas;i++) {
        char *linea=menu_view_basic_listing_punteros_lineas[i];
        //printf("%d : %s\n",i,linea);

        //Numero y espacio
        if (strlen(linea)>=6) {
            if (!strncmp(linea,buffer_linea_buscar,5)) {
                return i;
            }
        }
    }

    return -1;

}

//Retorna 0 si no encontrada
int menu_view_basic_listing_find_line(zxvision_window *w)
{
	char buffer_texto[10];

	buffer_texto[0]=0;
    menu_ventana_scanf("Line to search",buffer_texto,6);

    int linea=parse_string_to_number(buffer_texto);

    int indice=menu_view_basic_listing_find_line_aux(linea);

    if (indice>=0) {
        //printf("Encontrada\n");
        zxvision_set_offset_y(w,indice);
        return 1;
    }

    else {
        //printf("No encontrada\n");
        return 0;
    }

}

//Retorna indice de linea en array si encontrada
//-1 si no encontrada
int menu_view_basic_listing_find_text_aux(char *texto_buscar,int linea_inicial)
{

    int i;


    for (i=0;i<menu_view_basic_listing_total_lineas;i++) {
        char *linea=menu_view_basic_listing_punteros_lineas[i];
        //printf("%d : %s\n",i,linea);

        if (i>=linea_inicial) {
            char *encontrado=util_strcasestr(linea,texto_buscar);
            if (encontrado!=NULL) {
                return i;
            }
        }

    }

    return -1;

}

char menu_view_basic_listing_find_text_buffer_texto[30]="";

int menu_view_basic_listing_find_text_last_line=0;

//Retorna 0 si no encontrado
int menu_view_basic_listing_find_next_continue(zxvision_window *w)
{

    int indice=menu_view_basic_listing_find_text_aux(menu_view_basic_listing_find_text_buffer_texto,menu_view_basic_listing_find_text_last_line);

    if (indice>=0) {
        //printf("Encontrada\n");
        menu_view_basic_listing_find_text_last_line=indice;
        zxvision_set_offset_y(w,indice);
        return 1;
    }

    else {
        menu_view_basic_listing_find_text_last_line=0;
        //printf("No encontrada\n");
        return 0;
    }

}

//Retorna 0 si no encontrado
int menu_view_basic_listing_find_next(zxvision_window *w)
{

    menu_view_basic_listing_find_text_last_line++;

    return menu_view_basic_listing_find_next_continue(w);

}


//Retorna 0 si no encontrado
int menu_view_basic_listing_find_text(zxvision_window *w)
{

    menu_ventana_scanf("Text to search",menu_view_basic_listing_find_text_buffer_texto,30);

    menu_view_basic_listing_find_text_last_line=0;

    return menu_view_basic_listing_find_next_continue(w);

}

void menu_view_basic_listing_print_pointer_line(zxvision_window *ventana)
{
    int antes_writing_inverse_color=ventana->writing_inverse_color;

    ventana->writing_inverse_color=1;

    char buffer_length[100];

    //No mostrar valores negativos, que queda extraño
    //Esto pasa por ejemplo al cargar Breakout de ZX80 y ejecutarlo
    if (menu_view_basic_listing_memory_length<0) {
        strcpy(buffer_length,"Unknown");
    }

    else {
        sprintf(buffer_length,"%d",menu_view_basic_listing_memory_length);
    }


    char buffer_custom_pointer[100];
    buffer_custom_pointer[0]=0;

    if (menu_view_basic_listing_memory_enabled.v) {
        sprintf(buffer_custom_pointer,"[Custom] S~~tart: %d len~~gth: %s",menu_view_basic_listing_memory_pointer,buffer_length);
    }
    else {
        sprintf(buffer_custom_pointer,"[ Auto ] Start: %d length: %s",menu_view_basic_listing_memory_pointer,buffer_length);
    }


    zxvision_print_string_defaults_fillspc_format(ventana,1,MENU_VIEW_BASIC_LISTING_LINE_POINTER,
        "~~Start & Length %s",
        //(menu_view_basic_listing_memory_enabled.v ? 'X' : ' ' ),
        buffer_custom_pointer
    );
    ventana->writing_inverse_color=antes_writing_inverse_color;

}

int menu_view_basic_listing_last_width=-1;

void menu_view_basic_listing_overlay(void)
{

    menu_speech_set_tecla_pulsada(); //Si no, envia continuamente todo ese texto a speech

    //si ventana minimizada, no ejecutar todo el codigo de overlay
    if (menu_view_basic_listing_window->is_minimized) return;


    //Print....
    //Tambien contar si se escribe siempre o se tiene en cuenta contador_segundo...
    //printf("contador_segundo %d\n",contador_segundo);

    //esto hara ejecutar esto 5 veces por segundo
    if ( ((contador_segundo%200) == 0 && menu_view_basic_contador_segundo_anterior!=contador_segundo) ) {

        menu_view_basic_contador_segundo_anterior=contador_segundo;

        view_basic_parameters parameters;

        //Llamar aqui por saber si ha cambiado la longitud del programa basic
        menu_view_basic_listing_get_basic_parameters(&parameters);

        //printf("start add: %d\n",menu_view_basic_listing_memory_pointer);

        //Escribir esto continuamente para reflejar cambios en inicio de programa cuando se usa Interface 1
        menu_view_basic_listing_print_pointer_line(menu_view_basic_listing_window);

        //Obtener linea actual
        int linea,sentencia;
        debug_view_basic_get_current_line(&linea,&sentencia);

        char buffer_linea[100];


        if (MACHINE_IS_ZX8081) {
            if (linea>32767) {
                strcpy(buffer_linea,"Not in line");
            }
            else {
                sprintf(buffer_linea,"%5d",linea);
            }
        }

        else {
            //Spectrum
            if (linea>32767) {
                sprintf(buffer_linea,"Not in line. Sentence %d",sentencia);
            }
            else {
                sprintf(buffer_linea,"%5d:%3d",linea,sentencia);
            }
        }


        //Muestro aqui continuamente la linea y sentencia actual

        zxvision_print_string_defaults_fillspc_format(menu_view_basic_listing_window,1,MENU_VIEW_BASIC_LISTING_LINE_LINE,"Current line: %s",buffer_linea);


        z80_long_int crc32=menu_view_basic_listing_get_crc32();
        //printf("Obtenido crc: %X\n",crc32);
        if (crc32!=menu_view_basic_listing_last_crc32) {
            //printf("CRC modificado\n");
            menu_view_basic_listing_last_crc32=crc32;
            menu_view_basic_listing_modified=1;
        }

        //Si ha cambiado ancho, obtener de nuevo listado
        if (menu_view_basic_listing_last_width!=menu_view_basic_listing_window->visible_width) {
            menu_view_basic_listing_last_width=menu_view_basic_listing_window->visible_width;
            menu_view_basic_listing_modified=1;
        }

        if (menu_view_basic_listing_follow_current_line.v) {


            //Ver si ha cambiado linea actual

            if (linea>=0 && linea<=9999) {

                if (linea!=menu_view_basic_listing_follow_line_last) {
                    menu_view_basic_listing_follow_line_last=linea;

                    int indice=menu_view_basic_listing_find_line_aux(menu_view_basic_listing_follow_line_last);

                    if (indice>=0) {
                        //printf("Encontrada linea actual %d\n",linea);
                        zxvision_set_offset_y(menu_view_basic_listing_window,indice);

                    }

                    else {
                        //printf("No encontrada\n");

                    }
                }
            }
        }


    }

    if (menu_view_basic_listing_modified) {
        //printf("Obtener listado de nuevo\n");
        menu_view_basic_listing_modified=0;

        if (menu_view_basic_listing_results_buffer!=NULL) {
            free(menu_view_basic_listing_results_buffer);
            menu_view_basic_listing_results_buffer=NULL;
        }

        if (menu_view_basic_listing_results_buffer==NULL) {
            menu_view_basic_listing_results_buffer=util_malloc(VIEW_BASIC_MAX_BASIC_TEXT,"Can not allocate memory for view basic");
        }

        menu_view_basic_listing_get_basic(menu_view_basic_listing_results_buffer,0);
        menu_view_basic_listing_print_basic(menu_view_basic_listing_results_buffer,menu_view_basic_listing_window);

    }


    //Mostrar contenido
    zxvision_draw_window_contents(menu_view_basic_listing_window);

}




//Almacenar la estructura de ventana aqui para que se pueda referenciar desde otros sitios
zxvision_window zxvision_window_view_basic_listing;

int menu_view_basic_listing_set_number(char *title,int value)
{

    char string_texto[MAX_BREAKPOINT_CONDITION_LENGTH];
	sprintf(string_texto,"%d",value);

    menu_ventana_scanf(title,string_texto,MAX_BREAKPOINT_CONDITION_LENGTH);


	//int exp_par_evaluate_expression(char *entrada,char *salida)
	char buffer_salida[256]; //mas que suficiente
	char string_detoken[MAX_BREAKPOINT_CONDITION_LENGTH];

	int result=exp_par_evaluate_expression(string_texto,buffer_salida,string_detoken);
	if (result==0) {
		//menu_generic_message_format("Result","Parsed string: %s\nResult: %s",string_detoken,buffer_salida);
        value=parse_string_to_number(buffer_salida);
	}

	else if (result==1) {
		menu_error_message(buffer_salida);
	}

	else {
		menu_generic_message_format("Error","%s parsed string: %s",buffer_salida,string_detoken);
	}

    return value;

}

