void menu_view_basic_listing(MENU_ITEM_PARAMETERS)
{
	menu_espera_no_tecla();

    if (!menu_multitarea) {
        menu_warn_message("This window needs multitask enabled");
        return;
    }

    zxvision_window *ventana;
    ventana=&zxvision_window_view_basic_listing;

	//IMPORTANTE! no crear ventana si ya existe. Esto hay que hacerlo en todas las ventanas que permiten background.
	//si no se hiciera, se crearia la misma ventana, y en la lista de ventanas activas , al redibujarse,
	//la primera ventana repetida apuntaria a la segunda, que es el mismo puntero, y redibujaria la misma, y se quedaria en bucle colgado
	//zxvision_delete_window_if_exists(ventana);

    //Crear ventana si no existe
    if (!zxvision_if_window_already_exists(ventana)) {
        int xventana,yventana,ancho_ventana,alto_ventana,is_minimized,is_maximized,ancho_antes_minimize,alto_antes_minimize;

        if (!util_find_window_geometry("viewbasiclisting",&xventana,&yventana,&ancho_ventana,&alto_ventana,&is_minimized,&is_maximized,&ancho_antes_minimize,&alto_antes_minimize)) {
            ancho_ventana=30;
            alto_ventana=20;

            xventana=menu_center_x()-ancho_ventana/2;
            yventana=menu_center_y()-alto_ventana/2;
        }


        zxvision_new_window_gn_cim(ventana,xventana,yventana,ancho_ventana,alto_ventana,ancho_ventana-1,alto_ventana-2,"View Basic Listing",
            "viewbasiclisting",is_minimized,is_maximized,ancho_antes_minimize,alto_antes_minimize);

        ventana->can_be_backgrounded=1;

        //unas lineas superiores fijas
        ventana->upper_margin=VIEW_BASIC_HEADER_LINES;

    }

    //Si ya existe, activar esta ventana
    else {
        zxvision_activate_this_window(ventana);
    }

	zxvision_draw_window(ventana);

	z80_byte tecla;


	int salir=0;


    menu_view_basic_listing_window=ventana; //Decimos que el overlay lo hace sobre la ventana que tenemos aqui


    //cambio overlay
    zxvision_set_window_overlay(ventana,menu_view_basic_listing_overlay);


    //Toda ventana que este listada en zxvision_known_window_names_array debe permitir poder salir desde aqui
    //Se sale despues de haber inicializado overlay y de cualquier otra variable que necesite el overlay
    if (zxvision_currently_restoring_windows_on_start) {
        //printf ("Saliendo de ventana ya que la estamos restaurando en startup\n");
        return;
    }

    menu_view_basic_listing_modified=1;

    do {



        ventana->writing_inverse_color=1;

        char buffer_betabasic[100]="";
        if (MACHINE_IS_SPECTRUM) {
            sprintf(buffer_betabasic,"[%c] Show ~~BetaBasic ",(debug_view_basic_show_betabasic.v ? 'X' : ' ') );
        }


        zxvision_print_string_defaults_fillspc_format(ventana,1,MENU_VIEW_BASIC_LISTING_LINE_SETTINGS,
            "[%c] Show ~~address %s[%c] Fo~~llow line",
            (debug_view_basic_show_address.v ? 'X' : ' ' ),
            buffer_betabasic,
            (menu_view_basic_listing_follow_current_line.v ? 'X' : ' ')
        );

        zxvision_print_string_defaults_fillspc_format(ventana,1,MENU_VIEW_BASIC_LISTING_LINE_FIND,
            "~~e: Find lin~~e ~~f: ~~Find text ~~n: Find ~~next"
        );

        menu_view_basic_listing_print_pointer_line(ventana);


        ventana->writing_inverse_color=0;




		tecla=zxvision_common_getkey_refresh();

        zxvision_handle_cursors_pgupdn(ventana,tecla);


        switch (tecla) {
            case 'a':
                debug_view_basic_show_address.v ^=1;
                menu_view_basic_listing_modified=1;
            break;

            case 'b':
                if (MACHINE_IS_SPECTRUM) {
                    debug_view_basic_show_betabasic.v ^=1;
                    menu_view_basic_listing_modified=1;
                }
            break;

            case 'e':
                if (!menu_view_basic_listing_find_line(ventana)) {
                    menu_warn_message("Line not found");
                }
            break;

            case 'f':
                if (!menu_view_basic_listing_find_text(ventana)) {
                    menu_warn_message("Text not found");
                }
            break;

            case 'g':
                if (menu_view_basic_listing_memory_enabled.v) {
                    menu_view_basic_listing_set_length();
                }
            break;

            case 'l':
                menu_view_basic_listing_follow_current_line.v ^=1;
                menu_view_basic_listing_follow_line_last=-1;
            break;



            case 'n':
                if (menu_view_basic_listing_find_text_buffer_texto[0]==0) {
                    menu_warn_message("No text to search");
                }
                else {
                    if (!menu_view_basic_listing_find_next(ventana)) {
                        menu_warn_message("Text not found");
                    }
                }
            break;

            case 's':
                menu_view_basic_listing_memory_enabled.v ^=1;
            break;

            case 't':
                if (menu_view_basic_listing_memory_enabled.v) {
                    menu_view_basic_listing_set_pointer();
                }
            break;


            //Salir con ESC
            case 2:
                salir=1;
            break;

            //O tecla background
            case 3:
                salir=1;
            break;
        }


    } while (salir==0);


	util_add_window_geometry_compact(ventana);

	if (tecla==3) {
		zxvision_message_put_window_background();
	}

	else {
		zxvision_destroy_window(ventana);
	}


}







void menu_debug_save_binary(MENU_ITEM_PARAMETERS)
{

	char *filtros[2];

	filtros[0]="";
	filtros[1]=0;


	//guardamos directorio actual
	char directorio_actual[PATH_MAX];
	getcwd(directorio_actual,PATH_MAX);

	int ret;

	//Obtenemos ultimo directorio visitado
	if (binary_file_save[0]!=0) {
		char directorio[PATH_MAX];
		util_get_dir(binary_file_save,directorio);
		//printf ("strlen directorio: %d directorio: %s\n",strlen(directorio),directorio);

		//cambiamos a ese directorio, siempre que no sea nulo
		if (directorio[0]!=0) {
				debug_printf (VERBOSE_INFO,"Changing to last directory: %s",directorio);
				zvfs_chdir(directorio);
		}
	}

	ret=menu_filesel_save("Select File to Save",filtros,binary_file_save);

	//volvemos a directorio inicial
	zvfs_chdir(directorio_actual);


    if (ret==1) {

		//Ver si archivo existe y preguntar

      	if (si_existe_archivo(binary_file_save)) {

			if (menu_confirm_yesno_texto("File exists","Overwrite?")==0) return;

		}



		menu_debug_change_memory_zone();

		char string_direccion[10];
		sprintf (string_direccion,"%XH",save_binary_last_address);
		menu_ventana_scanf("Address: ",string_direccion,10);
		int valor_leido_direccion=parse_string_to_number(string_direccion);
		save_binary_last_address=valor_leido_direccion;


		char string_longitud[8];
		sprintf (string_longitud,"%d",save_binary_last_length);
		menu_ventana_scanf("Length: 0 - all",string_longitud,8);
		int valor_leido_longitud=parse_string_to_number(string_longitud);
		save_binary_last_length=valor_leido_longitud;


		save_binary_file(binary_file_save,valor_leido_direccion,valor_leido_longitud);


		//Y salimos de todos los menus
        salir_todos_menus=1;

    }


}




