void menu_realtape_record_input_analize_azimuth_scroll_string(char *texto)
{
    int longitud=strlen(texto);
    int final=longitud-1;

    int i;
    char inicial=texto[0];
    for (i=0;i<longitud-1;i++) {
        texto[i]=texto[i+1];
    }
    texto[final]=inicial;
}

